import React from 'react';
export const ModalLifecycleContext = React.createContext(undefined);
export const ModalLifecycleProvider = ({ children, willBlur }) => (<ModalLifecycleContext.Provider value={{
    didBlur: false,
    didFocus: !willBlur,
    willBlur,
    willFocus: !willBlur
}} children={children}/>);
