import React from 'react';
import { View } from 'react-native';
export const NavigationBarIos = ({ navigationBar }) => {
    let children;
    if (navigationBar.Component &&
        React.isValidElement(navigationBar.Component)) {
        children = navigationBar.Component;
    }
    else if (navigationBar.Component) {
        children = React.createElement(navigationBar.Component, {});
    }
    return (<View pointerEvents="box-none" style={{
        height: navigationBar.height,
        left: 0,
        position: 'absolute',
        right: 0,
        top: 0,
        zIndex: 1
    }}>
      {children}
    </View>);
};
